unsigned int CubeMap::createSkyBox(std::vector<std::string> faces)
{
	glGenTextures(1, &sb_textureID);
	glBindTexture(GL_TEXTURE_CUBE_MAP, sb_textureID);

	int width, height, channels;
	for (unsigned int i = 0; i < faces.size(); i++) {
		unsigned char* data = stbi_load((ENGINE_PATH + faces[i]).c_str(), &width, &height, &channels, 0);
		if (data) {
			glTexImage2D(GL_TEXTURE_CUBE_MAP_POSITIVE_X + i, 0, GL_RGB, width, height, 0, GL_RGB, GL_UNSIGNED_BYTE, data);
			stbi_image_free(data);
		}
		else {
			SEND_MSG(ERROR, "CUBEMAP", "Cubemap texture failed to load at path: " + faces[i]);
			stbi_image_free(data);
		}
	}
	glTexParameteri(GL_TEXTURE_CUBE_MAP, GL_TEXTURE_MIN_FILTER, GL_LINEAR);
	glTexParameteri(GL_TEXTURE_CUBE_MAP, GL_TEXTURE_MAG_FILTER, GL_LINEAR);
	glTexParameteri(GL_TEXTURE_CUBE_MAP, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_EDGE);
	glTexParameteri(GL_TEXTURE_CUBE_MAP, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_EDGE);
	glTexParameteri(GL_TEXTURE_CUBE_MAP, GL_TEXTURE_WRAP_R, GL_CLAMP_TO_EDGE);

	skybox_shader.loadShader("shaders/skybox_vertex.glsl", "shaders/skybox_fragment.glsl");

    glGenVertexArrays(1, &skyboxVAO);
    glGenBuffers(1, &skyboxVBO);
    glBindVertexArray(skyboxVAO);
    glBindBuffer(GL_ARRAY_BUFFER, skyboxVBO);
    glBufferData(GL_ARRAY_BUFFER, sizeof(skyboxVertices), &skyboxVertices, GL_STATIC_DRAW);
    glEnableVertexAttribArray(0);
    glVertexAttribPointer(0, 3, GL_FLOAT, GL_FALSE, 3 * sizeof(float), (void*)0);

	return sb_textureID;
}

void CubeMap::createCubemap()
{
	cm_textureID = createSkyBox(faces);
}

void CubeMap::render(glm::mat4& cameraViewMatrix)
{
    glDisable(GL_DEPTH_TEST);
    glDepthFunc(GL_LEQUAL);
	skybox_shader.use();
    glm::mat4 view;
	view = glm::mat4(glm::mat3(cameraViewMatrix));
    skybox_shader.setMat4("skyView", view);

    glBindVertexArray(skyboxVAO);
    glActiveTexture(GL_TEXTURE0);
    glBindTexture(GL_TEXTURE_CUBE_MAP, cm_textureID);
    glDrawArrays(GL_TRIANGLES, 0, 36);
    glBindVertexArray(0);
    glDepthFunc(GL_LESS);
    glEnable(GL_DEPTH_TEST);
}
